    private void btningresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btningresarActionPerformed
    char clave [] = txtpass.getPassword();
    String clavedef = new String (clave);
    if (txtusuario.getText().equals("admin") && clavedef.equals("12345")) {
        this.dispose();
        JOptionPane.showMessageDialog(null,"Bienvenido has ingresado satisfactoriamente al sistema" , "Mensaje de bienvenida" , JOptionPane.INFORMATION_MESSAGE);
        menu frmmenu = new menu();
        frmmenu.setVisible(true);
    }
    else if(intentos == 3) { //definimos el numero de intentos para ingresar datos correctos
      JOptionPane.showMessageDialog(null,"Ha excedido el numero de intentos para ingresar al sistema \n" + "Verifique sus datos de acceso, intentelo mas tarde", "Acceso denegado" , JOptionPane.ERROR_MESSAGE);  
      System.exit(0);      
    }
    else {
        JOptionPane.showMessageDialog(null,"El usuario o contrase単as son incorrectos, intentelo nuevamente \n Quedan " + (3 - intentos) + " intentos"); //mensaje por cada intento
        txtusuario.setText("");//limpiamos caja de texto usuario
        txtpass.setText("");//limpiamos caja de texto contrase単a
        txtusuario.requestFocus();//hacemos foco en la caja de texto usuario
        intentos = intentos + 1;//contabilizamos el numero de intentos
    }
    }//GEN-LAST:event_btningresarActionPerformed

